package com.syntax.class07;

public class LoopsIntro {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// Loop is  statement that executes block of code number of times based on the condition
		
		//  types of loop 
		/*
		 * while
		 * do while
		 * for loop
		 * enhanced for loop
		 * advanced for loop
		 * 
		 */

		//while loop - repeats block of code based on the condition
		
		int time = 10;
		
		if (time < 12) {
			System.out.println("Morning");
		}
		
		while (time < 12) { // this loop is infinite 
			System.out.println("morning");
		}
	}

}
