package com.syntax.class08;

public class LoopsPractice {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int sum = 0;

		for (int i = 1; i < 6; i++) {
			sum += i;
		}
		System.out.println(sum);
		
		System.out.println();
		
		int sum1 = 0;

		for (int i = 1; i < 6; i++) {
			sum1 += i;
			System.out.print(sum1+" ");
		}
	}

}
